import styled from "styled-components";

const SearchResultsStyles = styled.div`
  .results {
    display: grid;
    grid-gap: 20px;
    grid-template:
      ". . . ."
      ". .";
  }

  .results-item:nth-last-child(2) {
    grid-column-start: 2;
  }

  .results-item {
    height: 220px;
    width: 180px;
    position: relative;

    &__image {
      max-width: 100%;
      height: 100%;
      object-fit: cover;
    }

    &__btn {
      background: rgba(255, 255, 255, 0.75);
      border: none;
      border-radius: 50%;
      width: 30px;
      height: 30px;
      position: absolute;
      right: 8px;
      top: 8px;
      cursor: pointer;
    }
  }
`;
export default SearchResultsStyles;
