import { gql, useQuery } from "@apollo/client";
import React from "react";
import { useDebounce, useSearchForm } from "./hooks";

const RM_CHARACTERS = gql`
  query getRMcharacters($name: String!) {
    characters(filter: { name: $name }) {
      info {
        count
      }
      results {
        id
        name
        image
      }
    }
  }
`;

function App() {
  const { searchValue, onSearchChange } = useSearchForm();
  const query = useDebounce(searchValue);
  const { data, loading, error } = useQuery(RM_CHARACTERS, {
    variables: { name: "Rick" },
  });
  console.log(error?.message, data);
  return (
    <div className="App">
      <input
        placeholder="Enter a character name"
        onChange={onSearchChange}
        value={searchValue}
      />
    </div>
  );
}

export default App;
