import { gql, useQuery } from "@apollo/client";
import React from "react";
import { useDebounce, useSearchForm } from "./hooks";

const RM_CHARACTERS = gql`
  query getRMcharacters($name: String!) {
    characters(name: $name) {
      info {
        count
      }
      results {
        id
        name
        image
      }
    }
  }
`;

function App() {
  const { data, loading, error } = useQuery();
  let name = "Rick";
  const { searchValue, onSearchChange } = useSearchForm(RM_CHARACTERS, {
    variables: name,
  });
  const query = useDebounce(searchValue);
  console.log(query);
  return (
    <div className="App">
      <input
        placeholder="Enter a character name"
        onChange={onSearchChange}
        value={searchValue}
      />
    </div>
  );
}

export default App;
