import styled from "styled-components";

const SearchResultsStyles = styled.div`
  .results {
    display: grid;
    grid-gap: 20px;
    grid-template-columns: 1fr 1fr 1fr 1fr;
  }

  .results-item:nth-last-child(2) {
    grid-column-start: 2;
  }

  .results-item {
    height: 220px;
    width: 180px;
    position: relative;

    &__image {
      max-width: 100%;
      height: 100%;
      object-fit: cover;
    }

    &__btn {
      position: absolute;
      right: 0;
    }
  }
`;
export default SearchResultsStyles;
