import { useQuery, gql } from "@apollo/client";
import { useCallback, useEffect, useState } from "react";

export const useSearch: any = ({ searchQuery }: any) => {
  const [state, setState] = useState({
    characters: [],
    status: "IDLE",
    error: "",
  });

  const { error, data } = useQuery(RM_CHARACTERS);

  useEffect((): any => {
    if (searchQuery.length < 2) return;
    if (!error) {
      setState({
        characters: data,
        status: "SUCCESS",
        error: "",
      });
    } else {
      setState({
        characters: [],
        status: "ERROR",
        error: error.message,
      });
    }
    return state;
  }, [searchQuery, error, data]);
};

export const useDebounce = (value: string, delay: number = 300) => {
  const [debouncedValue, setDebouncedValue] = useState(value);

  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);
    return () => {
      clearTimeout(timer);
    };
  }, [value, delay]);

  return debouncedValue;
};

export const useSearchForm = () => {
  const [searchValue, setSearchValue] = useState("");

  const onSearchChange = useCallback((e) => setSearchValue(e.target.value), []);

  return {
    searchValue,
    onSearchChange,
  };
};
