import { useCallback, useEffect, useState } from "react";
import { ICharacter } from "./interfaces/ICharacter";
import { ICharactersQuery } from "./interfaces/ICharactersQuery";
import { getRandomItems } from "./utils";

export const useDebounce = (value: string, delay: number = 300) => {
  const [debouncedValue, setDebouncedValue] = useState(value);

  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);
    return () => {
      clearTimeout(timer);
    };
  }, [value, delay]);

  return debouncedValue;
};

export const useSearchForm = () => {
  const [searchValue, setSearchValue] = useState("");

  const onSearchChange = useCallback((e) => setSearchValue(e.target.value), []);

  return {
    searchValue,
    onSearchChange,
  };
};

export const useSkip = (query: string) => {
  const [skip, setSkip] = useState(false);

  useEffect(() => {
    if (query.length < 2) {
      setSkip(true);
    } else {
      setSkip(false);
    }
  }, [query]);

  return skip;
};

export const useChartacters = (excludedIds: number[], data: any) => {
  const [state, setState] = useState<ICharactersQuery>({
    characters: [],
  });

  useEffect(() => {
    if (!data) {
      setState({ characters: [] });
      return;
    }
    const filteredArray = data.characters.results.filter(
      (c: ICharacter) => !excludedIds.includes(parseInt(c.id, 10))
    );
    let arr = excludedIds.length ? filteredArray : data.characters.results;

    const charactersArray: ICharacter[] = arr.slice(0, 6);

    setState({ characters: charactersArray });
  }, [data]);

  return state;
};
