import { useQuery } from "@apollo/client";
import { useCallback, useEffect, useState } from "react";

export const useSearch = (searchQuery: any, gqlQuery: any) => {
  const [state, setState] = useState({
    characters: [],
    status: "IDLE",
    error: "",
  });

  const { data, loading, error } = useQuery(gqlQuery, {
    variables: { name: searchQuery },
  });

  useEffect(() => {
    if (searchQuery.length < 2) return;

    if (!error) {
      setState({
        characters: data.characters.results,
        status: "SUCCESS",
        error: "",
      });
    } else {
      setState({
        characters: [],
        status: "ERROR",
        error: error.message,
      });
    }
  }, [searchQuery]);

  return state;
};

export const useDebounce = (value: string | undefined, delay: number = 300) => {
  const [debouncedValue, setDebouncedValue] = useState(value);

  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);
    return () => {
      clearTimeout(timer);
    };
  }, [value, delay]);

  return debouncedValue;
};

export const useSearchForm = () => {
  const [searchValue, setSearchValue] = useState("");

  const onSearchChange = useCallback((e) => setSearchValue(e.target.value), []);

  return {
    searchValue,
    onSearchChange,
  };
};
