import React, { useCallback, useEffect, useState } from "react";
import { ICharacter } from "./interfaces/ICharacter";
import { ICharactersQuery } from "./interfaces/ICharactersQuery";

export const useDebounce = (value: string, delay: number = 300) => {
  const [debouncedValue, setDebouncedValue] = useState(value);

  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);
    return () => {
      clearTimeout(timer);
    };
  }, [value, delay]);

  return debouncedValue;
};

export const useSearchForm = () => {
  const [searchValue, setSearchValue] = useState("");

  const onSearchChange = useCallback((e) => setSearchValue(e.target.value), []);

  return {
    searchValue,
    onSearchChange,
  };
};

export const useSkip = (query: string) => {
  const [skip, setSkip] = useState(false);

  useEffect(() => {
    if (query.length < 2) {
      setSkip(true);
    } else {
      setSkip(false);
    }
  }, [query]);

  return skip;
};

export const useChartacters = (
  excludedIds: number[],
  data: any,
  queryError: any
) => {
  const [state, setState] = useState<ICharactersQuery>({
    characters: [],
    status: "IDLE",
    queryError: "",
  });

  useEffect(() => {
    if (!data) {
      setState({ characters: [], status: "IDLE", queryError: "" });
      if (queryError !== undefined)
        return setState({
          characters: [],
          status: "ERROR",
          queryError: queryError.message,
        });
      return;
    }
    const filteredArray = data.characters.results.filter(
      (c: ICharacter) => !excludedIds.includes(parseInt(c.id, 10))
    );
    let arr = excludedIds.length ? filteredArray : data.characters.results;

    const charactersArray: ICharacter[] = arr.slice(0, 6);

    setState({ characters: charactersArray, status: "SUCCES", queryError: "" });
  }, [data, queryError, excludedIds]);

  return state;
};

export const useChartactersFilter = (characters: ICharacter[]) => {
  const [deletedItems, setDeletedItems] = useState<number[]>([]);
  const [partyRick, setPartyRick] = useState<ICharacter>();
  const [partyMorty, setPartyMorty] = useState<ICharacter>();

  const getID = (e: React.MouseEvent) => {
    if (!e.target) return null;
    return e.currentTarget.getAttribute("data-id");
  };

  const onDelete = (e: React.MouseEvent): void => {
    const id = getID(e);

    if (!id) return;

    const deletedId = parseInt(id, 10);
    if (!deletedItems.includes(deletedId))
      setDeletedItems([...deletedItems, deletedId]);
  };

  const onSelect = (e: React.MouseEvent) => {
    const id = getID(e);

    if (!id) return;
    const selectedId = parseInt(id, 10);
    const selectedCharacter = characters.find(
      (c: ICharacter) => parseInt(c.id, 10) === selectedId
    );

    if (selectedCharacter) {
      /morty/i.test(selectedCharacter.name)
        ? setPartyMorty(selectedCharacter)
        : setPartyMorty(partyMorty);
      /rick/i.test(selectedCharacter.name)
        ? setPartyRick(selectedCharacter)
        : setPartyRick(partyRick);
    }
  };

  const getExcludedIds = (): number | null[] => {
    const selectedRick: number | null = partyRick
      ? parseInt(partyRick.id, 10)
      : null;
    const selectedMorty: number | null = partyMorty
      ? parseInt(partyMorty.id, 10)
      : null;
    return [...deletedItems, selectedMorty, selectedRick];
  };

  return {
    onDelete,
    onSelect,
    partyMorty,
    partyRick,
  };
};
