import { GET_RM_CHARACTERS } from "./queries/GetRmCharacters";
import { useQuery } from "@apollo/client";
import React, { useState } from "react";
import {
  useDebounce,
  useSearchForm,
  useSkip,
  useChartacters,
  useChartactersFilter,
} from "./hooks";

import SearchResults from "./components/SearchResults";
const deletedIds = [1, 6, 2, 85];
const selectedIds = [11, 4];
//const excludedIds: number[] = [...selectedIds, ...deletedIds];
const App = () => {
  const [excludedIds, setExcludedIds] = useState([]);

  const { searchValue, onSearchChange } = useSearchForm();

  const query = useDebounce(searchValue);
  const skip = useSkip(query);

  const { data, error } = useQuery(GET_RM_CHARACTERS, {
    variables: { name: query },
    skip,
  });

  const { characters, queryError, status } = useChartacters(
    excludedIds,
    data,
    error
  );

  const { onDelete, onSelect, partyMorty, partyRick } =
    useChartactersFilter(characters);

  setExcludedIds([1, 3, 5]);

  console.log(error?.message, data, query, characters, partyMorty, partyRick);
  return (
    <>
      <div className="App">
        <input
          placeholder="Enter a character name"
          onChange={onSearchChange}
          value={searchValue}
        />
      </div>
      <SearchResults
        characters={characters}
        status={status}
        queryError={queryError}
        onDelete={onDelete}
        onSelect={onSelect}
      />
    </>
  );
};

export default App;
