import { useQuery, gql } from "@apollo/client";
import React, { useState } from "react";
import { useDebounce, useSearch, useSearchForm } from "./hooks";

function App() {
  const { searchValue, onSearchChange } = useSearchForm();
  const searchQuery = useDebounce(searchValue);

  const RM_CHARACTERS = gql`
    query GetRMcharacters($searchQuery: String!) {
      characters(filter: { name: $searchQuery }) {
        info {
          count
        }
        results {
          id
          name
          image
        }
      }
    }
  `;
  const { error, data } = useQuery(RM_CHARACTERS, { variables: searchQuery });
  console.log(data, error);
  return (
    <div className="App">
      <input
        placeholder="Enter a character name"
        onChange={onSearchChange}
        value={searchValue}
      />
    </div>
  );
}

export default App;
