import { GET_RM_CHARACTERS } from "./queries/GetRmCharacters";
import { useQuery } from "@apollo/client";
import React, { useEffect, useState } from "react";
import { useDebounce, useSearchForm, useSkip } from "./hooks";

function App() {
  const { searchValue, onSearchChange } = useSearchForm();

  const query = useDebounce(searchValue);
  const skip = useSkip(query);
  const deletedIds = [1, 6, 2, 85];
  const selectedIds = [11, 4];

  const excludedIds: number[] = [...selectedIds, ...deletedIds];

  const [charState, setCharState] = useState({
    characters: [],
  });

  const { data, loading, error } = useQuery(GET_RM_CHARACTERS, {
    variables: { name: query },
    skip,
  });

  const getRandomItems = (count: number, initialArr: any[]) => {
    let itemsArr = [];

    for (let i = 0; i < count; i++) {
      const randomItem =
        initialArr[Math.floor(Math.random() * initialArr.length)];
      itemsArr.push(randomItem);
    }
    return itemsArr;
  };

  useEffect(() => {
    if (!data) {
      setCharState({ characters: [] });
      return;
    }
    const filteredArray = data.characters.results.filter(
      (c: any) => !excludedIds.includes(parseInt(c.id, 10))
    );
    let arr = excludedIds.length ? filteredArray : data.characters.results;

    const charactersArray: any = getRandomItems(6, arr);

    setCharState({ characters: charactersArray });
  }, [data]);

  console.log(error?.message, data, query, charState);
  return (
    <div className="App">
      <input
        placeholder="Enter a character name"
        onChange={onSearchChange}
        value={searchValue}
      />
    </div>
  );
}

export default App;
