import { gql, useQuery } from "@apollo/client";
import React, { useEffect, useState } from "react";
import { useDebounce, useSearchForm } from "./hooks";

const RM_CHARACTERS = gql`
  query getRMcharacters($name: String!) {
    characters(filter: { name: $name }) {
      info {
        count
      }
      results {
        id
        name
        image
      }
    }
  }
`;

function App() {
  const { searchValue, onSearchChange } = useSearchForm();
  const getQuery = (searchQuery: string) => {
    if (searchQuery.length < 2) return;
    return searchQuery;
  };

  const [skip, setSkip] = useState(false);
  const query = useDebounce(searchValue);

  useEffect(() => {
    if (!query && query.length < 2) {
      setSkip(true);
    } else {
      setSkip(false);
    }
  }, [query]);

  const { data, loading, error } = useQuery(RM_CHARACTERS, {
    variables: { name: query },
    skip,
  });

  console.log(error?.message, data, query);
  return (
    <div className="App">
      <input
        placeholder="Enter a character name"
        onChange={onSearchChange}
        value={searchValue}
      />
    </div>
  );
}

export default App;
